import React, { useState } from "react";
import "./App.css";

const createRow = number => {
  const row = [];
  let i = 0;
  while (i < number) {
    row.push(false);
    i++;
  }
  return row;
};

function App() {
  const [grid, setGrid] = useState(createRow(5000));

  const activeCeils = idx => {
    const newGrid = [...grid];
    newGrid[idx] = !grid[idx];
    setGrid(newGrid);
  };

  console.log(grid);
  return (
    <>
    <div className="App">
      {grid.map((elm, idx) => (
        <div
          className={grid[idx] ? "active" : "inactive"}
          key={idx}
          onClick={() => activeCeils(idx)}
        />
      ))}
    </div>
    </>
  );
}

export default App;
