import React, { useState, useEffect } from "react";
import "./App.css";

const generateGrid = (numRow, numCol) => {
  const row = [];
  let i = 0;
  while (i < numRow) {
    rows.push(Array.from(Array(numCols), () => 0));
    i++;
  }
  return row;
};

function App() {
  const [grid, setGrid] = useState(generateGrid(numberRow, numberCol));
  const [found, setFound] = useState(false)
  const [numberRow, setNumberRow] = useState(0)
  const [numberCol, setNumberCol] = useState(0)


  const activeCeils = idx => {
    const newGrid = [...grid];
    newGrid[idx] = !grid[idx];
    setGrid(newGrid);
  };

  const heart = () => console.log("RUN!")

  found && setInterval(() => {
        heart()
      }, 1000);

  return (
    <>
    <h1>GAME THE LIVE</h1>
    <input value={numberRow}/>
    <input value={numberCol} />
    <div className="App">
      {grid.map((elm, idx) => (
        <div
          className={grid[idx] ? "active" : "inactive"}
          key={idx}
          onClick={() => activeCeils(idx)}
        />
      ))}
    </div>
      <button onClick={() => setFound(!found)}>{found ? "STOP":"START"}</button>
    </>
  );
}

export default App;
