import React, { useState, useEffect, useCallback } from "react";
import produce from "immer";
import "./App.css";

const generateGrid = (numRows, numCols) => {
  const rows = [];
  for (let i = 0; i < numRows; i++) {
    rows.push(Array.from(Array(Number(numCols)), () => 0));
  }
  return rows;
};

function App() {
  const [numberRow, setNumberRow] = useState(Number)
  const [numberCol, setNumberCol] = useState(Number)

  const [found, setFound] = useState(false)
  const [grid, setGrid] = useState([]);

const handleChange = ({target: {id, value}}) => id === "col" ? setNumberCol(value) : setNumberRow(value)

useEffect(() => setGrid(() => generateGrid(numberRow, numberCol)), [numberRow, numberCol])


const runSimulation = useCallback(() => {
  if (!found) return 
}, [])

  return (
    <>
    <h1>GAME THE LIVE</h1>
      <input id="row" value={numberRow} onChange={handleChange} />
      <input id="col" value={numberCol} onChange={handleChange} />
      <div className="App" style={{gridTemplateColumns: `repeat(${numberCol}, 20px)`
      }}>
        {grid.map((rows, i) =>
          rows.map((col, k) => (
            <div
            className={col ? "active":"inactive"}
              key={`${i}-${k}`}
              onClick={() => {
                const newGrid = produce(grid, gridCopy => {
                  gridCopy[i][k] = grid[i][k] ? 0 : 1;
                });
                setGrid(newGrid);
              }}/>)))}
              </div>
      <button onClick={() => setFound(!found)}>{found ? "STOP":"START"}</button>
    </>
  );
}

export default App;
